mapper.select:
select no, title, writer, content, state, datetime
  from question 
 where no = #{no}

mapper.insert:
insert into question(
	title, writer, content, state
) values (
	#{title}, #{writer}, #{content}, #{state}
)

mapper.update:
update question 
   set title = #{title}, 
   	   writer = #{writer}, 
   	   content = #{content}
   	   state = #{state}
 where no = #{no}
